import TitleH1 from "./TitleH1";

const List = () => {
    const [items, setItems] = useState(data);
    const [displayItems, setDisplayItems] = useState(data);

    const handleFilter = (color) => {
        const filteredItems = items.filter(item => item.color === color);
        setDisplayItems(filteredItems);
    };

    const handleRest = () => {
        setDisplayItems(data);
    };

    return (
        <div>
            <TitleH1 name='Amelie' age={33} />
            <div>
                <button
                style={styles.button}
                onClick={() => handleFilter("Rouge")}>Rouge
                </button>
            </div>
            <button onClick={() => handleFilter("Vert")}>Vert</button>
            <button onClick={handleRest}>Reset</button>
            {displayItems.map((item) => (
                <div style={styles.card}>
                    <p>
                        {item.name} - {item.type} - {item.color}
                    </p>
                </div>
            ))}
        </div>
    );
};

